Events.OnConstruction:
(if (not (objIsAbandoned gSource))
	(block (minerCount)
		; Count the number of ferian miners for this base
		(setq minerCount (count (sysFindObject gSource "sH")))
		
		; If we've got less than our maximum, then built more
		(if (ls minerCount (objGetData gSource "maxMiners"))
			(block (newMiner)
				(setq newMiner
					(sysCreateShip &scFerianMiner; gSource &svDestructiveOrder; "ferian")
					)
				(objAddSubordinate gSource newMiner)
				(shpOrderMine newMiner gSource)
				)
			)
		)
	)
